INSERT INTO AdventurousToasts ([id], [name], [category], [description], [image], [dataAiHint], [price], [rating], [reviewText], [reviewerName]) VALUES (N'milkcrisp-hotdog-egg-toast-challenge', N'奶酥熱狗蛋', N'勇者挑戰', N'香甜奶酥與鹹香熱狗蛋的意外組合，淋上番茄醬，挑戰你對甜鹹搭配的想像力。', N'/Adventurous_Toast/Braveheart Challenge/milkcrisp-hotdog-egg-toast-challenge.jpeg', N'milkcrisp hotdog egg', 50, 4.5, N'奶酥提供了很濃的奶香和一點甜味，混合在熱狗蛋和番茄醬裡不僅不突兀，還增加不同層次的風味，甜和鹹的很協調不膩，首個超出預期的美味。', N'勇者鑑定團員B');
